package EjerciciosCiclos;

public class E11Ciclos {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
	int c1 = 0;
	int c2 = 0;
	
	while (c1 < 8) {
		while (c2 < 8) {
			
		System.out.print("*"); //println se quita el "ln" porque lo que queremos en una fila, no que vaya hacía abajo
			c2++;
			
		}
		c2=0;
		c1++;
		System.out.println();
		
	}
	
	

	}

}
